import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from 'axios';
import React ,{useEffect, useState} from "react";
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2'
import { data } from 'jquery';


const baseUrl = "http://localhost:3000";

export default function ListGenre(){

    const [dataGenre, setdataGenre] = useState([]);

    useEffect(() => {
        LoadGenre();
    }, []);

    function LoadGenre() {
        const url = baseUrl + "/genre/list";
        axios.get(url)
        .then(res => {
            if (res.data.success === true){
                const data = res.data.data;
                setdataGenre(data);
            }
            else {
                alert("Erro Web Service");
            }
        })
        .catch(error => {
            alert(error)
        });
    }

    return (
        <table className="table table-hover table-striped">
            <thead className = "thead-dark">
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Genre</th>
                </tr>
            </thead>
            <tbody>
                <LoadFillData/>
            </tbody>
        </table>
    );

    function LoadFillData(){
        return dataGenre.map((data, index) => {
            return(
            <tr key = {index}>
                <th>{index + 1}</th>
                <td>{data.description}</td>
                <td>
                    <Link className='btn btn-outline-info' to = {"/genre/update/" + data.id}>Update</Link>
                </td>
                <td>
                    <button className='btn btn-outline-danger' onClick={() => onDelete(data.id)}>Delete</button>
                </td>
            </tr>
        )
        });
    }

    function onDelete(genre){
        Swal.fire({
            title: 'Tem a certeza?',
            text: 'Esta ação é irreversível!',
            type: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Sim, apagar!',
            cancelButtonText: 'Não, manter'
        }).then((result) => {
            if (result.value) {
                sendDelete(genre)
            } else if (result.dismiss === Swal.DismissReason.cancel) {
                Swal.fire(
                'Canceled',
                'Genre was not deleted',
                'error'
                )
            }
        })
    }

    function sendDelete(genre){
        const url = baseUrl + "/genre/delete/" + genre
        axios.put(url)
        .then(response =>{
            if (response.data.success){
                Swal.fire(
                    'Deleted!',
                    'Genre was removed.',
                    'success'
                )
                LoadGenre();
            }
            else{
                Swal.fire(
                    'Canceled',
                    'Genre is assosiated to a movie!',
                    'error'
                )
            }
        })
        .catch ( error => {
            alert("Error 325 ")
        });
    }
}
