import React, { useEffect, useState } from "react";
import axios from 'axios';

const baseUrl = "http://localhost:3000";

export default function CreateMovie() {
  const [title, setTitle] = useState("");
  const [photo, setPhoto] = useState("");
  const [genre, setGenre] = useState("");
  const [description, setDescription] = useState("");
  const [dataGenre, setDataGenre] = useState([]);
  const [errorMessage, setErrorMessage] = useState(null); // State for error messages

  useEffect(() => {
    const url = baseUrl + "/genre/list";
    axios.get(url)
      .then(res => {
        if (res.data.success === true) {
          setDataGenre(res.data.data);
        } else {
          setErrorMessage("Error fetching genres. Please try again later.");
        }
      })
      .catch(error => {
        setErrorMessage("Error fetching genres. Please try again later.");
      });
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Basic validation
    if (!genre || !title || !photo || !description) {
      setErrorMessage("Please fill in all fields.");
      return;
    }

    try {
      const url = baseUrl + "/movie/create";
      const data = {
        title,
        photo,
        genre,
        description,
      };

      const response = await axios.post(url, data);

      if (response.data.success === true) {
        alert(response.data.message); // Success message (consider a more user-friendly approach)
        // Optionally clear the form fields here
        setTitle("");
        setPhoto("");
        setGenre("");
        setDescription("");
      } else {
        setErrorMessage(response.data.message); // Error message from backend
      }
    } catch (error) {
      setErrorMessage("An error occurred. Please try again later.");
      console.error(error); // Log the error for debugging
    }
  };

  // Load genres for dropdown (unchanged)
  function LoadGenre() {
    return dataGenre.map((data, index) => (
      <option key={index} value={data.id}>{data.description}</option>
    ));
  }

  return (
    <div>
      {/* Form with input fields and validation errors (if any) */}
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        {/* ... form fields and dropdown ... */}
        <button type="submit" className="btn btn-primary mt-2">Add Movie</button>
      </form>
    </div>
  );
}
