import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { Form, FormGroup, Label, Input, Button } from "reactstrap";npm

const baseUrl = "http://localhost:3000";

export default function UpdateGenre() {
  const [genre, setGenre] = useState("");
  const [description, setDescription] = useState("");
  const { genreId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const url = baseUrl + "/genre/get/" + genreId;

    axios.get(url)
      .then((res) => {
        if (res.data.success) {
          const data = res.data.data[0];
          setGenre(data.genre); // Assuming 'genre' field in response data
          setDescription(data.description);
        } else {
          alert("Error fetching genre data");
        }
      })
      .catch((error) => {
        alert("Error: " + error);
      });
  }, [genreId]);

  const handleUpdate = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    const url = baseUrl + "/genre/update/" + genreId;
    const data = { description };

    try {
      const response = await axios.put(url, data);

      if (response.data.success) {
        alert(response.data.message);
        navigate("/genres"); // Assuming route for genre list
      } else {
        alert("Error updating genre");
      }
    } catch (error) {
      alert("Error: " + error);
    }
  };

  return (
    <div className="container mt-3">
      <h2>Update Genre</h2>
      <Form onSubmit={handleUpdate}>
        <FormGroup>
          <Label for="description">Description:</Label>
          <Input
            type="text"
            name="description"
            id="description"
            value={description}
            onChange={(event) => setDescription(event.target.value)}
            placeholder="Enter new description"
          />
        </FormGroup>
        <Button type="submit" color="primary">
          Update
        </Button>
      </Form>
    </div>
  );
}
