import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import axios from 'axios';
import React,{useEffect, useState} from "react";
import { Link } from 'eact-router-dom';
import Swal from 'weetalert2';

const baseUrl = "http://localhost:3000";

export default function ListMovie(){

    const [movies, setMovies] = useState([]);

    useEffect(() => {
        LoadMovies();
    }, []);

    function LoadMovies() {
        const url = baseUrl + "/movie/list";
        axios.get(url)
       .then(res => {
            if (res.data.success === true){
                const data = res.data.data;
                setMovies(data);
            }
            else {
                alert("Erro Web Service");
            }
        })
       .catch(error => {
            alert(error)
        });
    }

    return (
        <table className="table table-hover table-striped">
            <thead className = "thead-dark">
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Título</th>
                    <th scope="col">Foto</th>
                    <th scope="col">Descrição</th>
                    <th scope="col">Género</th>
                </tr>
            </thead>
            <tbody>
                <LoadFillData/>
            </tbody>
        </table>
    );

    function LoadFillData(){
        return movies.map((movie, index) => {
            return(
            <tr key = {index}>
                <th>{index + 1}</th>
                <td>{movie.title}</td>
                <td> <img alt='MoviePhoto' style={{ width: '75px' }} src={movie.photo} /> </td>
                <td>{movie.description}</td>
                <td>{movie.genre? movie.genre.description : ''}</td>
                <td>
                    <Link className='btn btn-outline-info' to={"/movie/update/" + movie.id}>Editar</Link>
                </td>
                <td>
                    <button className='btn btn-outline-danger' onClick={() => onDeleteMovie(movie.id)}>Apagar</button>
                </td>
            </tr>
        )
        });
    }

    function onDelete(movieId){
        Swal.fire({
            title: 'Are you sure?',
            text: 'It won\'t be able to recover it later!',
            type: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete!',
            cancelButtonText: 'No, keep'
        }).then((result) => {
            if (result.value) {
                sendDelete(movieId)
            } else if (result.dismiss === Swal.DismissReason.cancel) {
                Swal.fire(
                'Canceled',
                'Movie was not deleted',
                'error'
                )
            }
        })
    }

    function sendDelete(movieId){
        const url = baseUrl + "/movie/delete/" + movieId
        axios.delete(url)
      .then(response =>{
            if (response.data.success){
                Swal.fire(
                    'Deleted!',
                    'Movie was removed.',
                    'uccess'
                )
                LoadMovies();
            }
            else{
                Swal.fire(
                    'Canceled',
                    'Movie is associated to a genre!',
                    'error'
                )
            }
        })
      .catch ( error => {
            alert("Error 325 ")
        });
    }
}
