import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const baseUrl = "http://localhost:3000";

export default function UpdateGenre() {
  const [Genre, setGenre] = useState("");
  const [Description, setDescription] = useState("");
  const { genreId } = useParams();

  useEffect(() => {
    const url = baseUrl + "/genre/get/" + genreId;
    axios.get(url)
     .then(res => {
        if (res.data.success) {
          const data = res.data.data[0];
          if (data) {
            setGenre(data.name);
            setDescription(data.description);
          } else {
            alert("Error web service");
          }
        }
      })
     .catch(error => {
        alert("Error server: " + error);
      });
  }, []);

  function SendUpdate() {
    const url = baseUrl + "/genre/update/" + genreId;
    const datapost = {
      name: Genre,
      description: Description,
    };
    axios.put(url, datapost)
     .then((response) => {
        if (response.data.success === true) {
          alert(response.data.message);
        } else {
          alert("Error");
        }
      })
     .catch((error)  => {
        alert("Error 34: " + error);
      });
  }

  return (
    <div>
      <div className="form-row justify-content-center">
        <div className="form-group col-md-6">
          <label htmlFor="inputPassword4">Name:</label>
          <input
            type="text"
            className="form-control"
            placeholder="Genre Name"
            value={Genre}
            onChange={(value) => setGenre(value.target.value)}
          />
        </div>
        <div className="form-group col-md-6">
          <label htmlFor="inputPassword4">Description:</label>
          <input
            type="text"
            className="form-control"
            placeholder="Description"
            value={Description}
            onChange={(value) => setDescription(value.target.value)}
          />
        </div>
      </div>
      <button type="submit" className="btn btn-primary mt-2" onClick={() => SendUpdate()}>
        Update
      </button>
    </div>
  );
}
